import logo from './logo.svg';
import './App.css';
import React, { useState } from 'react';


function App() {

  function Button() {
    const [isPressed, setIsPressed] = useState(false);

    const handleButtonPress = () => {
      setIsPressed(true);
    };

    const handleButtonRelease = () => {
      setIsPressed(false);
    };

  return (
    <div className="Main">
    <header>
        <p className="App-header"> Simple To Do List </p>
        <p className="Subheader"> Today is awesome day. The weather is awesome, you are awesome too! </p>
    </header>

    <div className="line2">
    <div>
    <button className="Button-black">+</button>
    </div>
    <div className="Buttons-style">
      <button className={isPressed ? 'pressed' : ''}
      onMouseDown={handleButtonPress}
      onMouseUp={handleButtonRelease}
      onMouseLeave={handleButtonRelease} 
      > To Do
      </button>
      <button className="Buttons">Done</button>
      <button className="Buttons">Trash</button>
    </div>
    </div>

    <header className="Subheader2">To Do</header>

<div className="Items-list">

<div className="item">
<input class="checkbox-style" type="checkbox" />

</div>
</div>
    </div>
  );
}
}

export default App;


